mapper.select:
select 

from biz_permission
where id = #{id,jdbcType=INTEGER}
  and menuId = #{menuid,jdbcType=INTEGER}

mapper.delete:
delete from biz_permission
where id = #{id,jdbcType=INTEGER}
  and menuId = #{menuid,jdbcType=INTEGER}

mapper.update:
update biz_permission


    name = #{name,jdbcType=VARCHAR},


    mark = #{mark,jdbcType=VARCHAR},


where id = #{id,jdbcType=INTEGER}
  and menuId = #{menuid,jdbcType=INTEGER}

update biz_permission
set name = #{name,jdbcType=VARCHAR},
  mark = #{mark,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
  and menuId = #{menuid,jdbcType=INTEGER}
